import React from "react";

function Heading( props ) {
    const [count, addNote] = props.useState;

    function increase() {
        addNote( count + 1 );
    }

    return(
        <header>
            <h1>Keeper of Notes</h1>
            <p>{props.totalNotes}</p>
            <button type="submit" onClick={increase}>+</button>
        </header>
    )
}

export default Heading;
